class IconModal extends React.Component {
    constructor(props) {
        super(props);
        console.warn("modal");
        this.state = {
            image: this.props.image,
            name: this.props.name,
            open: this.props.open,
        };
    }

    render() {
        this.state.image = this.props.image;
        this.state.name = this.props.name;
        this.state.open = this.props.open;
        console.warn("open: " + this.state.open);
        if (this.state.open)
        {
            console.warn("my image is: " + this.state.image + "\nmy name is: " + this.state.name);
            return (
                <div className="modal">
                    <div>
                        <span className="close">&times;</span>
                    </div>
                    <div>
                        <img src={this.state.image.fileName} className="modal-content" id="img01"></img>
                    </div>
                    <div id="caption">
                    </div>
                </div>
            );
        }
        else
        {
            return (
                null
            );
        }
    }
}


class HomepageHandler extends React.Component {

    constructor(props) {
        super(props);
        let files = this.props.files;
        let index = 0;
        let length = this.props.files.length;
        this.state = {
            files: files,
            modalFile: null,
            modalName: null,
            showModal: false,
            //index: index,
            length: length,
        };
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(file) {
        this.setState({
            modalFile: file,
            modalName: file.substring(0, file.length - 6),
            showModal: true,
        });
    }

    renderImage(file) {
        return (
            <img src={file} className="homepageIcon" onClick={() => this.handleClick(file)}></img>
        );
    }


    render() {
        const items = [];
        var tempItems = [];
        var i;
        for (i = 0; i < this.state.length; i++)
        {
            tempItems.push(
                <Col>{this.renderImage(this.state.files[i].fileName)}</Col>
            );

            if (((i + 1) % 6 == 0 || i == this.state.length - 1) && i != 0)
            {
                items.push(<Row>{tempItems}</Row>);
                tempItems = [];
            }
        }

        return (
            <div>
                <div>
                    <IconModal image={this.state.modalFile}
                               name={this.state.modalName}
                               open={this.state.showModal}
                    />
                </div>

                <div>
                    {items}
                </div>
            </div>
        );
    }
}


class Home extends React.Component {
    constructor(props)
    {
        super(props);

        this.state = {
            files: [],
            index: 0,
        };
        //runQuery('CharactersID', props)
    }

    componentDidMount() {
        console.warn("mount?")
        fetch('http://192.168.0.12:3000/fileNames')
          .then(response => response.json())
          .then(res => {
              this.setState({files: res});
          });
    }

    render() {
        for (let i = 0; i < this.state.files.length; i++)
        {
            console.warn(this.state.files[i].fileName);
        }
        if (!(this.state.files.length > 0))
            return null;

        return (
            <div>
                <Container className="themed-container champ" fluid={true}>
                <div>
                    <HomepageHandler files={this.state.files} />
                </div>
                </Container>
            </div>
        );
    }
}

export default Home;
