class HomepageHandler extends React.Component {

    constructor(props) {
        super(props);
        let files = this.props.files;
        let index = 0;
        let length = this.props.files.length;
        this.state = {
            files: files,
            //index: index,
            length: length,
        };
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(file) {
        console.warn({file});
    }

    renderImage(file) {
        //console.warn("IN RENDERIMAGE" + file);
        return (
            <img src={file} className="homepageIcon" onClick={this.handleClick(file)}></img>
        );
    }


    render() {
        const items = [];
        var tempItems = [];
        var i;
        for (i = 0; i < this.state.length; i++)
        {
            tempItems.push(
                <Col>{this.renderImage(this.state.files[i].fileName)}</Col>
            );

            if (((i + 1) % 6 == 0 || i == this.state.length - 1) && i != 0)
            {
                items.push(<Row>{tempItems}</Row>);
                tempItems = [];
            }
        }

        return (
            <div>
                {items}
            </div>
        );
    }
}


class Home extends React.Component {
    constructor(props)
    {
        super(props);

        this.state = {
            files: [],
            index: 0,
        };
        //runQuery('CharactersID', props)
    }

    componentDidMount() {
        console.warn("mount?")
        fetch('http://192.168.0.12:3000/fileNames')
          .then(response => response.json())
          .then(res => {
              this.setState({files: res});
          });
    }

    render() {
        for (let i = 0; i < this.state.files.length; i++)
        {
            console.warn(this.state.files[i].fileName);
        }
        if (!(this.state.files.length > 0))
            return null;

        return (
            <div>
                <Container className="themed-container champ" fluid={true}>
                <div>
                    <HomepageHandler files={this.state.files} />
                </div>
                </Container>
            </div>
        );
    }
}

export default Home;
